import React from 'react';
import { useSelector } from 'react-redux';

import styles from './Search.module.scss';
import searchSVG from '../../assets/img/search-svgrepo-com.svg';
import clearSVG from '../../assets/img/close_delete_icon.svg';
import { AppContext } from '../../App';

const Search = () => {
  const { searchValue, setSerachValue } = React.useContext(AppContext);

  return (
    <div className={styles.root}>
      <img className={styles.searchSvg} alt="search" src={searchSVG} />
      <input
        value={searchValue}
        onChange={(event) => setSerachValue(event.target.value)}
        placeholder="Пошук"
      />
      {searchValue && (
        <img
          onClick={() => setSerachValue('')}
          className={styles.clearSvg}
          alt="clear"
          src={clearSVG}
        />
      )}
    </div>
  );
};

export default Search;
