import { createSlice } from '@reduxjs/toolkit';
import axios from 'axios';

const initialState = {
  itemsList: [],
  itemType: 0,
  itemSize: 0,
  isLoading: true,
};

export const itemsSlice = createSlice({
  name: 'items',
  initialState,
  reducers: {
    setItems: (state, action) => {
      state.itemsList = action.payload;
    },
    setItemType: (state, action) => {
      state.itemType = action.payload;
    },
    setItemSize: (state, action) => {
      state.itemSize = action.payload;
    },
    setIsLoading: (state, action) => {
      state.isLoading = action.payload;
    },
    fetchItems: async (state, action) => {
      const { categoryValue, searchValue, sortValue, currentPage } = action.payload;
      state.isLoading = true;

      const sortValues = [
        'rating',
        'price&order=asc',
        'price&order=desc',
        'name&order=asc',
        'name&order=desc',
      ];
      const category = categoryValue === 0 ? '' : `&category=${categoryValue}`;
      const sort = `&sortBy=${sortValues[sortValue]}`;
      const search = searchValue ? `&search=${searchValue}` : '';
      const filters = sort + category + search;

      try {
        const { data } = await axios.get(
          `https://6335ad248aa85b7c5d20b4d1.mockapi.io/items?page=${currentPage}&limit=4${filters}`,
        );
        state.setItems(data);
      } catch (error) {
        alert('Помилка: ' + error);
      } finally {
        state.setIsLoading(false);
      }
    },
  },
});

export const { setItemType, setItemSize, setItems, fetchItems } = itemsSlice.actions;

export default itemsSlice.reducer;
