import React from 'react';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

import styles from './PizzaDetails.module.scss';
import { fetchItemById } from '../../redux/slices/itemsSlice';

const PizzaDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const { imageUrl, name } = useSelector((state) => state.items.item);

  React.useEffect(() => {
    dispatch(fetchItemById({ id }));
  }, []);

  return (
    <div className={styles.root}>
      <img className={styles.image} src={imageUrl} alt="Pizza" />
      <div className={styles.description}>
        <h2>{name}</h2>
        <p>Опис: надзвичайно смачна піца!</p>
        <p>Склад: тісто, начинка =)</p>
      </div>
    </div>
  );
};

export default PizzaDetails;
