import Categories from './Categories';
import Sort from './Sort';
import PizzaBlock from './PizzaBlock';

const Content = () => {
  return (
    <div class="content">
      <div class="container">
        <div class="content__top">
          <Categories />
          <Sort />
        </div>
        <h2 class="content__title">Усі піци</h2>
        <div class="content__items">
          <PizzaBlock name={'Чізбургер-піца'} price={399} />
        </div>
      </div>
    </div>
  );
};

export default Content;
