import React from 'react';

import styles from './Search.module.scss';
import searchSVG from '../../assets/img/search-svgrepo-com.svg';
import clearSVG from '../../assets/img/close_delete_icon.svg';

const Search = ({ searchValue, setSerachValue }) => {
  const clearInput = () => {
    setSerachValue('');
  };

  return (
    <div className={styles.root}>
      <img className={styles.searchSvg} alt="search" src={searchSVG} />
      <input
        value={searchValue}
        onChange={(event) => setSerachValue(event.target.value)}
        placeholder="Пошук"
      />
      {searchValue.length > 0 && (
        <img onClick={clearInput} className={styles.clearSvg} alt="clear" src={clearSVG} />
      )}
    </div>
  );
};

export default Search;
