import React from 'react';
import uniqid from 'uniqid';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Search from '../components/Search';

const Home = () => {
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [categoryValue, setCategoryValue] = React.useState(0);
  const [sortValue, setSortValue] = React.useState(0);

  React.useEffect(() => {
    setIsLoading(true);

    const sortValues = [
      'rating',
      'price&order=asc',
      'price&order=desc',
      'name&order=asc',
      'name&order=desc',
    ];
    const categoryFetching = categoryValue === 0 ? '' : `&category=${categoryValue}`;
    const sortFetching = `?sortBy=${sortValues[sortValue]}`;

    fetch(`https://6335ad248aa85b7c5d20b4d1.mockapi.io/items${sortFetching + categoryFetching}`)
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
        setIsLoading(false);
      });

    window.scrollTo(0, 0);
  }, [categoryValue, sortValue]);

  return (
    <>
      <div className="content__top">
        <Categories categoryVal={categoryValue} onClickCategory={(val) => setCategoryValue(val)} />
        <Sort sortType={sortValue} onClickSortType={(val) => setSortValue(val)} />
      </div>
      <h2 className="content__title">Усі піци</h2>
      <div className="content__items">
        {isLoading
          ? [...Array(6)].map(() => <Skeleton key={uniqid()} />)
          : items.map((obj) => <PizzaBlock key={obj.id} {...obj} />)}
      </div>
    </>
  );
};

export default Home;
