import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  cartItems: [],
  allPrice: 0,
};

export const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    // addItem: (state, action) => {
    //   state.cartItems.push(action.payload);
    //   state.allPrice = state.cartItems.reduce((counter, obj) => {
    //     return obj.price + counter;
    //   }, 0);
    addItem: (state, action) => {
      const findItemByID = state.cartItems.find((obj) => obj.id === action.payload.id);
      const findItemByParams = state.cartItems.find(
        (obj) => obj.type === action.payload.type && obj.size === action.payload.size,
      );

      const obj = { ...action.payload, count: 1 };

      if (findItemByID && findItemByParams) {
        findItemByID.count += 1;
      } else {
        state.cartItems.push(obj);
      }

      state.allPrice = state.cartItems.reduce((counter, obj) => {
        return obj.count * obj.price + counter;
      }, 0);
    },
    clearCart: (state) => {
      state.cartItems = [];
    },
    setAllPrice: (state, action) => {
      state.allPrice = action.payload;
    },
    incrementItemCount: (state, action) => {
      const { id, count } = action.payload;
      const findItem = state.cartItems.find((obj) => obj.id === id && obj.count === count);

      if (findItem) {
        state.cartItems.count += 1;
      }
    },
    decrementItemCount: (state, action) => {
      const { id, count } = action.payload;
      const findItem = state.cartItems.find((obj) => obj.id === id && obj.count === count);

      if (findItem) {
        if (state.cartItems.count > 1) {
          state.cartItems.count -= 1;
        }
      }
    },
  },
});

export const { addItem, setAllPrice, clearCart, incrementItemCount, decrementItemCount } =
  cartSlice.actions;

export default cartSlice.reducer;
