import React from 'react';
import uniqid from 'uniqid';
import { useSelector, useDispatch } from 'react-redux';
import { setCategoryValue } from '..redux/slices/counterSlice';

const Categories = () => {
  const categoryVal = useSelector((state) => state.filter.categoryValue);
  const dispatch = useDispatch();

  const categories = ['Усі', "М'ясні", 'Вегетаріанські', 'Гриль', 'Гострі', 'Закриті'];

  return (
    <div className="categories">
      <ul>
        {categories.map((category, i) => (
          <li
            onClick={() => dispatch(setCategoryValue(i))}
            className={categoryVal === i ? 'active' : ''}
            key={uniqid()}>
            {category}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Categories;
