import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchItems = createAsyncThunk(
  'items/fetchItemsStatus',
  async ({ filters, currentPage, id, thunkAPI }) => {
    console.log(id);

    const { data } = await axios.get(
      !id
        ? `https://6335ad248aa85b7c5d20b4d1.mockapi.io/items?page=${currentPage}&limit=4${filters}`
        : `https://6335ad248aa85b7c5d20b4d1.mockapi.io/items/${id}`,
    );

    return thunkAPI.fulfillWithValue(id);

    return data;
  },
);

const initialState = {
  itemsList: [],
  itemType: 0,
  itemSize: 0,
  status: 'loading', // loading, success, error
};

export const itemsSlice = createSlice({
  name: 'items',
  initialState,
  reducers: {
    setItems: (state, action) => {
      state.itemsList = action.payload;
    },
    setItemType: (state, action) => {
      state.itemType = action.payload;
    },
    setItemSize: (state, action) => {
      state.itemSize = action.payload;
    },
  },
  extraReducers: {
    [fetchItems.pending]: (state) => {
      state.status = 'loading';
      state.itemsList = [];
    },
    [fetchItems.fulfilled]: (state, action) => {
      console.log('action ', action);
      state.itemsList = action.payload;
      state.status = 'success';
    },
    [fetchItems.rejected]: (state) => {
      state.status = 'error';
      state.itemsList = [];
    },
  },
});

export const { setItemType, setItemSize, setItems } = itemsSlice.actions;

export default itemsSlice.reducer;
