import React from 'react';
// libs
import uniqid from 'uniqid';
import { useSelector, useDispatch } from 'react-redux';
import qs from 'qs';
import { useNavigate } from 'react-router-dom';
// components
import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Pagination from '../components/Pagination';
import ErrorGetItems from '../components/ErrorGetItems';
// redux slices
import { fetchItems, selectItemsList } from '../redux/slices/itemsSlice';
import { setFilters } from '../redux/slices/filterSlice';

type FilterSlice = {
  categoryValue: number;
  sortValue: number;
  searchValue: string;
  currentPage: number;
}

type ItemsSlice = {
  itemsList: any;
  status: string;
}

const Home: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isSearchUrl = React.useRef<boolean>(false);
  const isFirstMounted = React.useRef<boolean>(false);

  const { categoryValue, sortValue, searchValue, currentPage }: FilterSlice = useSelector(
    (state: any) => state.filter,
  );
  const { itemsList, status }: ItemsSlice = useSelector(selectItemsList);

  const fetching = async () => {
    const sortValues = [
      'rating',
      'price&order=asc',
      'price&order=desc',
      'name&order=asc',
      'name&order=desc',
    ];
    const category = categoryValue === 0 ? '' : `&category=${categoryValue}`;
    const sort = `&sortBy=${sortValues[sortValue]}`;
    const search = searchValue ? `&search=${searchValue}` : '';
    const filters = sort + category + search;

    dispatch(fetchItems({ filters, currentPage }));
  };

  React.useEffect(() => {
    window.scrollTo(0, 0);

    fetching();

    isSearchUrl.current = false;
  }, [categoryValue, sortValue, searchValue, currentPage, window.location.search]);

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));

      dispatch(setFilters({ ...params }));

      isSearchUrl.current = true;
    }
  }, []);

  React.useEffect(() => {
    if (isFirstMounted.current) {
      const queryString = qs.stringify({
        categoryValue,
        sortValue,
        currentPage,
      });

      navigate(`?${queryString}`);
    } else {
      navigate('/');
      isFirstMounted.current = true;
    }
  }, [categoryValue, sortValue, currentPage]);

  console.log(isFirstMounted.current);

  const pizzas = itemsList.map((obj) => <PizzaBlock key={obj.id} {...obj} />);
  const skeleton = [...Array(4)].map(() => <Skeleton key={uniqid()} />);

  return (
    <>
      <div className="content__top">
        <Categories />
        <Sort />
      </div>
      <h2 className="content__title">Усі піци</h2>

      {status === 'error' ? (
        <ErrorGetItems />
      ) : (
        <div className="content__items">{status !== 'success' ? skeleton : pizzas}</div>
      )}
      {itemsList.length > 0 && <Pagination />}
    </>
  );
};

export default Home;
