// libs
import React from 'react';
import uniqid from 'uniqid';
import { useSelector, useDispatch } from 'react-redux';
import qs from 'qs';
import { useNavigate } from 'react-router-dom';
// components
import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Pagination from '../components/Pagination';
// redux slices
import { fetchItems } from '../redux/slices/itemsSlice';
import { setFilters } from '../redux/slices/filterSlice';

const Home = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isSearchUrl = React.useRef(false);
  const isFirstMounted = React.useRef(false);

  const [isLoading, setIsLoading] = React.useState(true);

  const { categoryValue, sortValue, searchValue, currentPage } = useSelector(
    (state) => state.filter,
  );
  const items = useSelector((state) => state.items.itemsList);

  const fetching = async () => {
    setIsLoading(true);

    const sortValues = [
      'rating',
      'price&order=asc',
      'price&order=desc',
      'name&order=asc',
      'name&order=desc',
    ];
    const category = categoryValue === 0 ? '' : `&category=${categoryValue}`;
    const sort = `&sortBy=${sortValues[sortValue]}`;
    const search = searchValue ? `&search=${searchValue}` : '';
    const filters = sort + category + search;

    try {
      dispatch(fetchItems({ filters, currentPage }));
    } catch (error) {
      alert('Помилка: ' + error);
    } finally {
      setIsLoading(false);
    }
  };

  React.useEffect(() => {
    window.scrollTo(0, 0);

    if (!isSearchUrl.current) {
      fetching();
    }

    isSearchUrl.current = false;
  }, [categoryValue, sortValue, searchValue, currentPage]);

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1));

      dispatch(setFilters({ ...params }));

      isSearchUrl.current = true;
    }
  }, []);

  React.useEffect(() => {
    if (isFirstMounted.current) {
      const queryString = qs.stringify({
        categoryValue,
        sortValue,
        currentPage,
      });

      navigate(`?${queryString}`);
    } else {
      isFirstMounted.current = true;
    }
  }, [categoryValue, sortValue, currentPage]);

  const pizzas = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />);
  const skeleton = [...Array(4)].map(() => <Skeleton key={uniqid()} />);

  return (
    <>
      <div className="content__top">
        <Categories />
        <Sort />
      </div>
      <h2 className="content__title">Усі піци</h2>
      <div className="content__items">{isLoading ? skeleton : pizzas}</div>
      <Pagination />
    </>
  );
};

export default Home;
