import React from 'react';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

import styles from './PizzaDetails.module.scss';
import { fetchItemById, selectItemsList } from '../../redux/slices/itemsSlice';

import ErrorGetItems from '../../components/ErrorGetItems';
import Skeleton from './Skeleton';

const PizzaDetails: React.FC = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const { item, status } = useSelector(selectItemsList);
  const { imageUrl, name } = item;

  React.useEffect(() => {
    dispatch(fetchItemById({ id }));
  }, []);

  return (
    <div className={styles.root}>
      {status === 'error' ? (
        <ErrorGetItems />
      ) : status === 'loading' ? (
        <Skeleton />
      ) : (
        <>
          <img className={styles.image} src={imageUrl} alt="Pizza" />
          <div className={styles.description}>
            <h2>{name}</h2>
            <p>Опис: надзвичайно смачна піца!</p>
            <p>Склад: тісто, начинка.</p>
          </div>
        </>
      )}
    </div>
  );
};

export default PizzaDetails;
