import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { RootState } from '../store';

type ItemType = {
  id: number;
  imageUrl: string;
  name: string;
  types: number[];
  sizes: number[];
  price: number;
  rating: number;
};

interface ItemsSliceType {
  itemsList: ItemType[];
  itemType: number;
  itemSize: number;
  status: "loading" | "success" | "error";
  item: ItemType | null;
}

export const fetchItems = createAsyncThunk<ItemType[], Record<string, string>>(
  'items/fetchItemsStatus',
  async (params) => {
    const { filters, currentPage } = params;
    const { data } = await axios.get<ItemType[]>(
      `https://6335ad248aa85b7c5d20b4d1.mockapi.io/items?page=${currentPage}&limit=4${filters}`,
    );

    return data;
  },
);

export const fetchItemById = createAsyncThunk<ItemType[], Record<string, string>>('items/fetchItemByIdStatus', async (params) => {
  const { id } = params;
  const { data } = await axios.get<ItemType[]>(`https://6335ad248aa85b7c5d20b4d1.mockapi.io/items/${id}`);

  return data;
});

const initialState: ItemsSliceType = {
  itemsList: [],
  itemType: 0,
  itemSize: 0,
  status: 'loading', 
  item: null,
};

export const itemsSlice = createSlice({
  name: 'items',
  initialState,
  reducers: {
    setItems: (state, action) => {
      state.itemsList = action.payload;
    },
    setItemType: (state, action) => {
      state.itemType = action.payload;
    },
    setItemSize: (state, action) => {
      state.itemSize = action.payload;
    },
  },
  extraReducers: (builder) => {
    //fetchItems status
      builder.addCase(fetchItems.pending, (state) => {
        state.status = 'loading';
        state.itemsList = [];
      });

      builder.addCase(fetchItems.fulfilled, (state, action) => {
        state.itemsList = action.payload;
        state.status = 'success';
      });

      builder.addCase(fetchItems.rejected, (state) => {
        state.status = 'error';
        state.itemsList = [];
      });
      //fetchItemById status
  },
  // extraReducers: {
 
  //   //fetchItemById status
  //   [fetchItemById.pending]: (state) => {
  //     state.status = 'loading';
  //     state.item = {};
  //   },
  //   [fetchItemById.fulfilled]: (state, action) => {
  //     state.item = action.payload;
  //     state.status = 'success';
  //   },
  //   [fetchItemById.rejected]: (state) => {
  //     state.status = 'error';
  //     state.item = {};
  //   },
  // },
});

export const selectItemsList = (state: RootState) => state.items;

export const { setItemType, setItemSize, setItems } = itemsSlice.actions;

export default itemsSlice.reducer;
