import React from 'react';
import uniqid from 'uniqid';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';

const Home = () => {
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [categoryValue, setCategoryValue] = React.useState(0);

  React.useEffect(() => {
    fetch('https://6335ad248aa85b7c5d20b4d1.mockapi.io/items')
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
      })
      .finally(() => {
        setIsLoading(false);
      });

    window.scrollTo(0, 0);
  }, []);

  return (
    <>
      <div className="content__top">
        <Categories categoryVal={categoryValue} onClickCategory={() => setCategoryValue(i)} />
        <Sort />
      </div>
      <h2 className="content__title">Усі піци</h2>
      <div className="content__items">
        {isLoading
          ? [...Array(6)].map(() => <Skeleton key={uniqid()} />)
          : items.map((obj) => <PizzaBlock key={obj.id} {...obj} />)}
      </div>
    </>
  );
};

export default Home;
