import React from 'react';
import uniqid from 'uniqid';
import { AppContext } from '../App';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Pagination from '../components/Pagination';

const Home = () => {
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [categoryValue, setCategoryValue] = React.useState(0);
  const [sortValue, setSortValue] = React.useState(0);
  const [currentPage, setCurrentPage] = React.useState(1);

  const { searchValue } = React.useContext(AppContext);

  React.useEffect(() => {
    setIsLoading(true);

    const sortValues = [
      'rating',
      'price&order=asc',
      'price&order=desc',
      'name&order=asc',
      'name&order=desc',
    ];
    const categoryFetching = categoryValue === 0 ? '' : `&category=${categoryValue}`;
    const sortFetching = `&sortBy=${sortValues[sortValue]}`;
    const searchFetching = searchValue ? `&search=${searchValue}` : '';

    fetch(
      `https://6335ad248aa85b7c5d20b4d1.mockapi.io/items?page=${currentPage}&limit=4${
        sortFetching + categoryFetching + searchFetching
      }`,
    )
      .then((res) => {
        return res.json();
      })
      .then((json) => {
        setItems(json);
        setIsLoading(false);
      });

    window.scrollTo(0, 0);
  }, [categoryValue, sortValue, searchValue, currentPage]);

  const pizzas = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />);
  const skeleton = [...Array(4)].map(() => <Skeleton key={uniqid()} />);

  return (
    <>
      <div className="content__top">
        <Categories categoryVal={categoryValue} onClickCategory={(val) => setCategoryValue(val)} />
        <Sort sortType={sortValue} onClickSortType={(val) => setSortValue(val)} />
      </div>
      <h2 className="content__title">Усі піци</h2>
      <div className="content__items">{isLoading ? skeleton : pizzas}</div>
      <Pagination currentPage={currentPage} setCurrentPage={setCurrentPage} />
    </>
  );
};

export default Home;
