import React from 'react';

const Categories = () => {
  const [activeCategory, setActiveCategory] = React.useState(0);
  const categories = [
    {
      name: 'Усі',
      isActive: true,
    },
    {
      name: "М'ясні",
      isActive: false,
    },
    {
      name: 'Вегетаріанські',
      isActive: false,
    },
    {
      name: 'Гриль',
      isActive: false,
    },
    {
      name: 'Гострі',
      isActive: false,
    },
    {
      name: 'Закриті',
      isActive: false,
    },
  ];

  return (
    <div className="categories">
      <ul>
        <li className="active">Усі</li>
        <li>М'ясні</li>
        <li>Вегетаріанські</li>
        <li>Гриль</li>
        <li>Гострі</li>
        <li>Закриті</li>
      </ul>
    </div>
  );
};

export default Categories;
